#!/bin/sh
nix run github:nix-community/nixos-anywhere --extra-experimental-features 'nix-command flakes' -- --flake '.#nas' --generate-hardware-config nixos-facter ./machines/nas/facter.json --ssh-option StrictHostKeyChecking=no --ssh-option UserKnownHostsFile=/dev/null --phases kexec,disko,install root@192.168.1.25